<style>
#g-drag {
  position: relative;
  background-color: #e8e8e8;
  width: 300px;
  height: 34px;
  line-height: 34px;
  text-align: center;
}
#g-drag .handler {
  position: absolute;
  top: 0px;
  left: 0px;
  width: 40px;
  height: 32px;
  border: 1px solid #ccc;
  cursor: move;
}
#g-drag .handler_bg {
  background: #fff
    url('data:image/png;base64,')
    no-repeat center;
}
#g-drag .handler_ok_bg {
  background: #fff
    url('data:image/png;base64,')
    no-repeat center;
}
#g-drag .drag_bg {
  background-color: #7ac23c;
  height: 34px;
  width: 0px;
}
#g-drag .drag_text {
  position: absolute;
  top: 0px;
  width: 300px;
  -moz-user-select: none;
  -webkit-user-select: none;
  user-select: none;
  -o-user-select: none;
  -ms-user-select: none;
}
</style>

<template>
  <div id="g-drag" :style="{color:color}" @mousemove="mouseMove" @mouseleave="mouseOver">
    <div class="drag_bg" :style="{width:width + 'px'}"></div>
    <div class="drag_text">{{text}}</div>
    <div :class="{handler:true, handler_bg:!isOk, handler_ok_bg:isOk}" @mousedown.left="mouseDown" @mouseup.left="mouseUp" :style="{left:left + 'px'}"></div>
  </div>
</template>

<script>
// handler_ok_bg
export default {
  name: 'Slide',
  data () {
    return {
      color: '#333',
      isMove: false,
      text: '拖动滑块验证',
      clientX: 0,
      currentClientX: 0,
      offset: 260,
      // 是否验证成功
      isOk: false
    }
  },
  computed: {
    width () {
      if (this.isOk) {
        return this.offset
      }
      let result = this.currentClientX - this.clientX
      if (result > this.offset) {
        result = this.offset
      }
      return result > 0 ? result : 0
    },
    left () {
      if (this.isOk) {
        return this.offset
      }
      let result = this.currentClientX - this.clientX
      if (result > this.offset) {
        result = this.offset
      }
      return result > 0 ? result : 0
    }
  },
  methods: {
    mouseDown (event) {
      this.isMove = true
      this.clientX = event.clientX
    },
    mouseUp (event) {
      this.isMove = false
      this.clientX = 0
      this.currentClientX = 0
    },

    mouseMove (event) {
      if (this.isMove && !this.isOk) {
        this.currentClientX = event.clientX
      }
      if ((this.currentClientX - this.clientX) > this.offset && !this.isOk) {
        this.isOk = true
        this.text = '验证通过'
        this.color = '#fff'
        this.$emit('validate')
      }
    },
    mouseOver () {
      if (this.isMove && !this.isOk) {
        this.isMove = false
        this.clientX = 0
        this.currentClientX = 0
      }
    }
  }
}
</script>
